    20â†’/// Proof: A formal verification that a property holds
    21â†’///
    22â†’/// This represents a mathematical proof that a specification property is true.
    23â†’#[derive(Debug, Clone, Serialize, Deserialize)]
    24â†’pub struct Proof {
    25â†’    /// Unique identifier
    26â†’    pub id: String,
    27â†’
    28â†’    /// The property being proved
    29â†’    pub property: Property,
    30â†’
    31â†’    /// The proof method used
    32â†’    pub method: ProofMethod,
    33â†’
    34â†’    /// Proof status
    35â†’    pub status: ProofStatus,
    36â†’
    37â†’    /// Proof steps (for human readability)
    38â†’    pub steps: Vec<ProofStep>,
    39â†’
    40â†’    /// Metadata
    41â†’    pub metadata: HashMap<String, String>,
    42â†’}
    43â†’
    44â†’/// Property: A statement about specifications that can be proven
    45â†’#[derive(Debug, Clone, Serialize, Deserialize)]
    46â†’pub enum Property {
    47â†’    /// No contradiction exists between two specifications
    48â†’    /// Proves: âˆƒx. (x âˆˆ A1 âˆ§ x âˆˆ A2) (admissible sets have non-empty intersection)
    49â†’    Consistency { spec_a: String, spec_b: String },
    50â†’
    51â†’    /// A specification is satisfiable
    52â†’    /// Proves: âˆƒx. x âˆˆ A (admissible set is non-empty)
    53â†’    Satisfiability { spec: String },
    54â†’
    55â†’    /// A specification implies another
    56â†’    /// Proves: A1 âŠ† A2 (admissible set inclusion)
    57â†’    Implication { antecedent: String, consequent: String },
    58â†’
    59â†’    /// A specification is complete for a domain
    60â†’    /// Proves: D âŠ† D_S (domain fully covered)
    61â†’    Completeness { spec: String, domain: String },
    62â†’
    63â†’    /// A layer transformation preserves semantics
    64â†’    /// Proves: f(A_source) âŠ† A_target (transform is sound)
    65â†’    TransformSoundness { source: String, target: String, transform: String },
    66â†’}
