    68â†’/// ProofMethod: How a proof was obtained
    69â†’#[derive(Debug, Clone, Serialize, Deserialize)]
    70â†’pub enum ProofMethod {
    71â†’    /// Constraint solving (lightweight built-in solver)
    72â†’    ConstraintSolving {
    73â†’        solver: String,
    74â†’        constraints: Vec<String>,
    75â†’    },
    76â†’
    77â†’    /// SMT solver (Z3, CVC4, etc.)
    78â†’    SMTSolver {
    79â†’        solver: String,
    80â†’        formula: String,
    81â†’    },
    82â†’
    83â†’    /// Theorem prover (Lean4, Coq, Isabelle)
    84â†’    TheoremProver {
    85â†’        prover: String,
    86â†’        proof_script: String,
    87â†’    },
    88â†’
    89â†’    /// Property-based testing (QuickCheck-style)
    90â†’    PropertyTesting {
    91â†’        iterations: usize,
    92â†’        counterexample: Option<String>,
    93â†’    },
    94â†’
