   101â†’/// ProofStatus: Result of proof attempt
   102â†’#[derive(Debug, Clone, Copy, PartialEq, Eq, Serialize, Deserialize)]
   103â†’pub enum ProofStatus {
   104â†’    /// Property is proven true
   105â†’    Proven,
   106â†’
   107â†’    /// Property is proven false (counterexample found)
   108â†’    Refuted,
   109â†’
   110â†’    /// Unable to prove or refute
   111â†’    Unknown,
   112â†’
   113â†’    /// Proof attempt in progress
   114â†’    Pending,
   115â†’}
