   117â†’/// ProofStep: One step in a proof derivation
   118â†’#[derive(Debug, Clone, Serialize, Deserialize)]
   119â†’pub struct ProofStep {
   120â†’    /// Step description
   121â†’    pub description: String,
   122â†’
   123â†’    /// Justification (rule, axiom, lemma)
   124â†’    pub justification: String,
   125â†’}
