   457â†’    /// Get all proofs
   458â†’    pub fn list_proofs(&self) -> Vec<&Proof> {
   459â†’        self.proofs.values().collect()
   460â†’    }
   461â†’
   462â†’    /// Get a specific proof
   463â†’    pub fn get_proof(&self, id: &str) -> Option<&Proof> {
   464â†’        self.proofs.get(id)
   465â†’    }
   466â†’
   467â†’    /// Get proofs for a specific specification
   468â†’    pub fn get_proofs_for_spec(&self, spec_id: &str) -> Vec<&Proof> {
   469â†’        self.proofs.values().filter(|p| {
   470â†’            match &p.property {
   471â†’                Property::Consistency { spec_a, spec_b } => {
   472â†’                    spec_a == spec_id || spec_b == spec_id
   473â†’                }
   474â†’                Property::Satisfiability { spec } => spec == spec_id,
   475â†’                Property::Implication { antecedent, consequent } => {
   476â†’                    antecedent == spec_id || consequent == spec_id
   477â†’                }
   478â†’                Property::Completeness { spec, .. } => spec == spec_id,
   479â†’                Property::TransformSoundness { source, target, .. } => {
   480â†’                    source == spec_id || target == spec_id
   481â†’                }
   482â†’            }
   483â†’        }).collect()
   484â†’    }
   485â†’}
