            let root = root_node.unwrap();
            println!("üìã Tracing relationships for:");
            println!("   [{}] {}: {}", &root.id[..8], format_node_kind(root.kind), root.content);

            let layer_str = if let Some(l) = root.metadata.get("formality_layer") {
