                return Ok(());
            }

            let root_node = root.unwrap();
            println!("📋 Tracing relationships for:");
            println!("   [{}] {}: {}", root_node.id, node_kind_name(root_node.kind), root_node.content);

            let layer_str = if let Some(l) = root_node.metadata.get("formality_layer") {
