echo "  Log: $LOG_FILE"

specd > "$LOG_FILE" 2>&1 &
echo $! > "$PID_FILE"

sleep 1

if kill -0 $(cat "$PID_FILE") 2>/dev/null; then
    echo "✓ specd started (PID: $(cat "$PID_FILE"))"
else
    echo "✗ Failed to start specd. Check $LOG_FILE"
    rm "$PID_FILE"
    exit 1
fi
