use std::collections::HashMap;
use uuid::Uuid;

#[derive(Debug, Clone, Copy, PartialEq, Eq, Hash, Serialize, Deserialize)]
pub enum NodeKind {
    Assertion,
    Constraint,
    Scenario,
    Definition,
    Domain,
}

#[derive(Debug, Clone, Copy, PartialEq, Eq, Hash, Serialize, Deserialize)]
pub enum EdgeKind {
    Refines,
    DependsOn,
    Contradicts,
    DerivesFrom,
    Synonym,
    Composes,
    Formalizes,  // Target is a more formal version of source
    Transform,   // Function f: maps spec from source universe to target universe
}

#[derive(Debug, Clone, PartialEq, Serialize, Deserialize)]
pub struct SpecNodeData {
    pub id: String,
    pub content: String,
    pub kind: NodeKind,
    pub metadata: HashMap<String, String>,
    #[serde(default)]
    pub created_at: i64,
    #[serde(default)]
    pub modified_at: i64,
    #[serde(default)]
    pub formality_layer: u8,  // 0=natural, 1=structured, 2=formal, 3=executable
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SpecEdgeData {
    pub id: String,
    pub kind: EdgeKind,
    pub metadata: HashMap<String, String>,
    #[serde(default)]
    pub created_at: i64,
}

#[derive(Debug, Clone)]
pub struct Contradiction {
    pub node_a: SpecNodeData,
    pub node_b: SpecNodeData,
    pub explanation: String,
}

#[derive(Debug, Clone)]
pub struct Omission {
    pub description: String,
    pub related_nodes: Vec<SpecNodeData>,
}

#[derive(Debug, Clone)]
pub struct LayerInconsistency {
    pub source: SpecNodeData,
    pub target: SpecNodeData,
    pub explanation: String,
}

#[derive(Debug, Clone)]
pub struct InterUniverseInconsistency {
    pub universe_a: String,  // Universe identifier (from metadata)
    pub universe_b: String,
    pub spec_a: SpecNodeData,
    pub spec_b: SpecNodeData,
    pub transform_path: Vec<String>,  // Edge IDs forming the transformation path
